
public class Ex03_07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		float b;
		
		a = (int) 123.45f; //정수형 inf에 실수값을 입력하면 오류가 나오기 때문에 int를 ()로 처리함
		b = 200;
		
		System.out.printf("a의 값 ==> %d \n", a);
		System.out.printf("b의 값 ==> %f \n", b);
		
	}

}
