
public class Ex04_14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.printf("%d를 오른쪽 1회 시프트 하면 %d이다. \n",  a, a>>1);
		System.out.printf("%d를 오른쪽 2회 시프트 하면 %d이다. \n",  a, a>>2);
		System.out.printf("%d를 오른쪽 3회 시프트 하면 %d이다. \n",  a, a>>3);
		System.out.printf("%d를 오른쪽 4회 시프트 하면 %d이다. \n",  a, a>>4);	
	}

}
