
public class Ex05_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 200;
		
		if (a < 100) {
			System.out.printf("100보다 작다 \n");
			System.out.printf("참이면 이문장이 보이겠죠\n");//if조건문이 참이면 실행됨
		}
		else {
			System.out.printf("100보다 크다 \n");
			System.out.printf("거짓이면 이 문장도 보이겠죠\n"); //if조건문이 거짓이면 실행됨
		}
		System.out.printf("프로그램 끝! \n");
		
	}

}
