
public class Ex06_08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int total=0, i; //합계 누적할 total에 0으로 초기화 설정
		
		for (i=1; i <=10; ++i);{
		total += i; //total 변수에 1부터 10까지 반복해서 누적 total=total+1 과 같다.
	}
		 System.out.printf("1에서 10까지의 합 : %d \n", total);
	}

}
